import React, { useState } from 'react';
import axios from 'axios';

function useInput(initialValue, validation) {
  const [value, setValue] = useState(initialValue);
  const [error, setError] = useState('');

  function handleChange(event) {
    const inputValue = event.target.value;
    setValue(inputValue);
    setError(validation(inputValue));
  }

  return { value, onChange: handleChange, error };
}

function isValidEmail(email) {
  return /^\S+@\S+\.\S+$/.test(email) ? '' : '올바른 이메일 형식이어야 합니다.';
}

function isValidGitHubLink(link) {
  return /^(https?|ftp):\/\/[^\s/$.?#].[^\s]*$/.test(link) ? '' : '올바른 깃허브 URL이어야 합니다.';
}

function Request() {
  const emailInput = useInput('', isValidEmail);
  const projectNameKrInput = useInput('', (value) => (value.trim() ? '' : '필수 항목입니다.'));
  const projectNameEnInput = useInput('', (value) => (/^[a-zA-Z\s]+$/.test(value) ? '' : '영어로만 작성되어야 합니다.'));
  const teamNameKrInput = useInput('', (value) => (value.trim() ? '' : '필수 항목입니다.'));
  const teamNameEnInput = useInput('', (value) => (/^[a-zA-Z\s]+$/.test(value) ? '' : '영어로만 작성되어야 합니다.'));
  const githubLinkInput = useInput('', isValidGitHubLink);

  const [agree, setAgree] = useState(true);

  function handleSubmit(e) {
    e.preventDefault();

    // 모든 입력 필드의 유효성 검사 확인
    if (
      emailInput.error ||
      projectNameKrInput.error ||
      projectNameEnInput.error ||
      teamNameKrInput.error ||
      teamNameEnInput.error ||
      githubLinkInput.error
    ) {
      return;
    }

    // 요청 데이터 구성
    const dataToSubmit = {
      email: emailInput.value,
      projectNameKr: projectNameKrInput.value,
      projectNameEn: projectNameEnInput.value,
      teamNameKr: teamNameKrInput.value,
      teamNameEn: teamNameEnInput.value,
      githubLink: githubLinkInput.value,
    };

    // Axios 요청 보내기
    axios.post('https://your-api-url.com', dataToSubmit)
      .then((res) => console.log(res))
      .catch((err) => console.error(err));
  }

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-200">
      <form className="p-6 bg-white rounded shadow-md w-[500px]" onSubmit={handleSubmit}>
        xquare 서버에 프로젝트 배포를 신청하기 위한 폼입니다.<br/>
        배포 Action 사용을 위한 엑세스 키 및 DB 계정 정보는 데브옵스 팀 검토 후 이메일을 통해 24시간 내에 전송됩니다.<br/><br/>

        {/* 이메일 입력 필드 */}
        <InputField
          label="이메일"
          type="email"
          {...emailInput}
          required
        />

        {/* 프로젝트명 (한글) 입력 필드 */}
        <InputField
          label="프로젝트명 (한글)"
          type="text"
          {...projectNameKrInput}
          required
        />

        {/* 프로젝트명 (영어) 입력 필드 */}
        <InputField
          label="프로젝트명 (영어)"
          type="text"
          {...projectNameEnInput}
          required
        />

        {/* 팀명 (한글) 입력 필드 */}
        <InputField
          label="팀명 (한글)"
          type="text"
          {...teamNameKrInput}
          required
        />

        {/* 팀명 (영어) 입력 필드 */}
        <InputField
          label="팀명 (영어)"
          type="text"
          {...teamNameEnInput}
          required
        />

        {/* 배포할 프로젝트의 깃허브 링크 입력 필드 */}
        <InputField
          label="배포할 프로젝트의 깃허브 링크"
          type="text"
          {...githubLinkInput}
          required
        />

        {/* Redis 사용 여부 체크박스 입력 필드 */}
        <CheckboxField
          label="Redis 사용 여부"
          checked={agree}
          onChange={() => setAgree(!agree)}
        />

        <button
          className="w-full px-4 py-2 mt-6 font-bold text-white bg-blue-500 rounded-md hover-bg-blue-700 focus:outline-none focus:shadow-outline"
          type="submit"
        >
          제출하기
        </button>
      </form>
    </div>
  );
}

export default Request;
