import React, { useState } from 'react';
import axios from 'axios';

// 컴포넌트: 입력 필드 레이블 및 입력 요소를 포함
function InputField({ label, type, value, onChange }) {
  return (
    <label className="block mb-2 text-sm text-gray-700">
      {label}
      <input
        className="w-full px-3 py-2 mt-1 text-sm leading-tight text-gray-700 border rounded shadow appearance-none focus:outline-none focus:shadow-outline"
        type={type}
        value={value}
        onChange={onChange}
      />
    </label>
  );
}

// 컴포넌트: 체크박스 입력 필드
function CheckboxField({ label, checked, onChange }) {
  return (
    <label className="block mb-2 text-sm text-gray-700">
      {label}
      <br/>
      <input
        className="ml-[5px] transform scale-150"
        type="checkbox"
        checked={checked}
        onChange={onChange}
      />
    </label>
  );
}

export const Request = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [reason, setReason] = useState('');
  const [agree, setAgree] = useState(true);

  const handleSubmit = (e) => {
    e.preventDefault();

    const dataToSubmit = {
      name,
      email,
      reason,
    };

    axios.post('https://your-api-url.com', dataToSubmit)
      .then((res) => console.log(res))
      .catch((err) => console.error(err));
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-200">
      <form className="p-6 bg-white rounded shadow-md w-[500px]" onSubmit={handleSubmit}>
        xquare 서버에 프로젝트 배포를 신청하기 위한 폼입니다.<br/>
        배포 Action 사용을 위한 엑세스 키 및 DB 계정 정보는 데브옵스 팀 검토 후 이메일을 통해 24시간 내에 전송됩니다.<br/>
        <br/>

        {/* InputField 컴포넌트 사용 */}
        <InputField
          label="이름"
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />

        {/* InputField 컴포넌트 사용 */}
        <InputField
          label="이메일"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        {/* CheckboxField 컴포넌트 사용 */}
        <CheckboxField
          label={<>Redis 사용 여부</>}
          checked={agree}
          onChange={(e) => setAgree(!agree)}
        />

        <button
          className="w-full px-4 py-2 mt-6 font-bold text-white bg-blue-500 rounded-md hover:bg-blue-700 focus:outline-none focus:shadow-outline"
          type="submit"
        >
          제출하기
        </button>
      </form>
    </div>
  );
}
